import { useState } from 'react';

export default function Swatch(props) {
  const [red, setRed] = useState(props.red);
  const [green, setGreen] = useState(props.green);
  const [blue, setBlue] = useState(props.blue);

  const rgbColor = `rgb(${red},${green},${blue})`;
  console.log(rgbColor);

  return (
    <>
      <div style={{
        'background-color': rgbColor,
        height: '4rem',
        width: '4rem',
      }} />
      <button onClick={() => setRed(red + 1)}>
        More Red
      </button>
      <button onClick={() => setRed(red - 1)}>
        Less Red
      </button>
      <button onClick={() => setGreen(green + 1)}>
        More Green
      </button>
      <button onClick={() => setGreen(green - 1)}>
        Less Green
      </button>
      <button onClick={() => setBlue(blue + 1)}>
        More Blue
      </button>
      <button onClick={() => setBlue(blue - 1)}>
        Less Blue
      </button>
    </>
  );
}
