const COLORS = {
  'Cyan': '#00FFFF',
  'Magenta': '#FF00FF',
  'Yellow': '#ffff00',
  'Black': '#000000'
};
export default function ColorToggle({ color, setColor, name }) {

  return (
    <div style={{
      'border-radius': '5rem',
      height: '10rem',
      width: '10rem',
      'background-color': COLORS[name],
    }} >
      <button disabled={color === 100} onClick={() => setColor(color + 1)}>
        More {name}
      </button>
      <button disabled={color === 0} onClick={() => setColor(color - 1)}>
        Less {name}
      </button>
    </div>
  );
}
