import { useState } from 'react';
import cmykRgb from 'cmyk-rgb';

export default function Swatch(props) {
  const [cyan, setCyan] = useState(props.cyan);
  const [magenta, setMagenta] = useState(props.magenta);
  const [yellow, setYellow] = useState(props.yellow);
  const [black, setBlack] = useState(props.black);

  const [r, g, b] = cmykRgb([cyan / 100, magenta / 100, yellow / 100, black / 100]);

  console.log(r,g,b);

  return (
    <div
      style={{
      'background-color': `rgb(${r}, ${g}, ${b})`,
      height: '100vh',
      width: '100vw',
    }}>
      <button disabled={magenta === 100} onClick={() => setMagenta(magenta + 1)}>
        More Magenta
      </button>
      <button disabled={magenta === 0} onClick={() => setMagenta(magenta - 1)}>
        Less Magenta
      </button>
      <button disabled={cyan === 100} onClick={() => setCyan(cyan + 1)}>
        More Cyan
      </button>
      <button disabled={cyan === 0} onClick={() => setCyan(cyan - 1)}>
        Less Cyan
      </button>
      <button disabled={yellow === 100} onClick={() => setYellow(yellow + 1)}>
        More Yellow
      </button>
      <button disabled={yellow === 0} onClick={() => setYellow(yellow - 1)}>
        Less Yellow
      </button>
      <button disabled={black === 100} onClick={() => setBlack(black + 1)}>
        More Black
      </button>
      <button disabled={black === 0} onClick={() => setBlack(black - 1)}>
        Less Black
      </button>
    </div>
  );
}
