import { useState } from 'react';

import Swatch from './components/Swatch'
import Mix from './components/Swatch'

import './App.css';

function hashCode(input) {
  return [...input].reduce((hash, chr) => {
    return hash * 33 + chr.charCodeAt(0);
  }, 0);
}

function generateRandom(maxInt, stringParam) {
  return hashCode(stringParam) % maxInt;
}

function getTodaysColor(string) {
  console.log(string);
  return {
    cyan: generateRandom(100, string.substr(0, 4)),
    magenta: generateRandom(100, string.substr(4, 4)),
    yellow: generateRandom(100, string.substr(8, 4)),
    black: generateRandom(100, string.substr(12, 4)),
  };
}

function App() {
  const [step, setStep] = useState(0);
  const date = new Date(new Date().toLocaleDateString()).valueOf();
  const todaysColor = getTodaysColor((date * date).toString().substr(0, 16));

  return step === 0 ? (
    <Swatch todaysColor={todaysColor} />
  ) : (
    <Mix />
  );
}

export default App;
