import cmykRgb from 'cmyk-rgb';

const min = 1;
const max = 100;

function calculateScore(score) {
  const logScore = (score - min) / (max - min) * (Math.log10(max) - Math.log10(min)) + Math.log10(min);

  return Math.pow(10, logScore);
}

export default function Score({ myColor, todaysColor
}) {
  const [myR, myG, myB] = cmykRgb([myColor.cyan / 100, myColor.magenta / 100, myColor.yellow / 100, myColor.black / 100]);

  const [r, g, b] = cmykRgb([todaysColor.cyan / 100, todaysColor.magenta / 100, todaysColor.yellow / 100, todaysColor.black / 100]);

  const diff = Math.abs(myR - r) + Math.abs(myG - g) + Math.abs(myB - b);

  const score = Math.round((1 - (diff / 765)) * 100);
  const myScore = Math.round(calculateScore(score));

  const gradient = `linear-gradient(to right, rgba(${myR},${myG},${myB},1) 50%, rgba(${r}, ${g}, ${b}, 1) 50%)`;

  let topScore = localStorage.getItem(new Date().toLocaleDateString());

  if (myScore > topScore) {
    localStorage.setItem(new Date().toLocaleDateString(), myScore);
    topScore = myScore;
  }

  return (
    <div
      style={{
        background: gradient,
        height: '100vh',
        width: '100vw',
        display: 'flex',
        alignContent: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        textAlign: 'center',
        textShadow: '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
        fontSize: '4rem',
        color: '#fff',
      }}>
      <div>Score: {myScore}</div>
      <div>Top Score: {topScore}</div>
    </div>
  );
}
