import { useState } from 'react';

const COLORS = {
  'Cyan': '#00FFFF',
  'Magenta': '#FF00FF',
  'Yellow': '#ffff00',
  'Black': '#000000'
};
export default function ColorToggle({ color, setColor, name }) {
  const [plus, setPlus] = useState(false);
  const [minus, setMinus] = useState(false);


  while (plus) {
    setInterval(() => setColor(color + 1), 500);
  }

  while (minus) {
    setInterval(() => setColor(color - 1), 500);
  }


  return (
    <div style={{
      'border-radius': '5rem',
      height: '10rem',
      width: '10rem',
      'background-color': COLORS[name],
      display: 'flex',
      'align-items': 'center',
      'justify-content': 'center',
    }} >
      <button
        disabled={color === 100}
        onMouseDown={() => setPlus(true)}
        onMouseUp={() => setPlus(false)}
      >
        More {name}
      </button>
      <button
        disabled={color === 0}
        onMouseDown={() => setMinus(true)}
        onMouseUp={() => setMinus(false)}
      >
        Less {name}
      </button>
    </div>
  );
}
