import { useState } from 'react';
import cmykRgb from 'cmyk-rgb';
import ColorToggle from './ColorToggle';

const initColor = () => Math.round(Math.random() * 100);

export default function Swatch() {
  const [cyan, setCyan] = useState(initColor());
  const [magenta, setMagenta] = useState(initColor());
  const [yellow, setYellow] = useState(initColor());
  const [black, setBlack] = useState(initColor());

  const [r, g, b] = cmykRgb([cyan / 100, magenta / 100, yellow / 100, black / 100]);

  console.log(r,g,b);

  return (
    <div
      style={{
      backgroundColor: `rgb(${r}, ${g}, ${b})`,
      height: '100vh',
      width: '100vw',
    }}>
      <ColorToggle color={cyan} setColor={setCyan} name="Cyan" />
      <ColorToggle color={magenta} setColor={setMagenta} name="Magenta" />
      <ColorToggle color={yellow} setColor={setYellow} name="Yellow" />
      <ColorToggle color={black} setColor={setBlack} name="Black" />
    </div>
  );
}
