import { useState } from 'react';

const COLORS = {
  'cyan': '#00FFFF',
  'magenta': '#FF00FF',
  'yellow': '#ffff00',
  'black': '#000000'
};
export default function ColorToggle({ color, setMyColor, name }) {
  const [coloring, setColoring] = useState(null);

  const lessDisabled = color <= 0;
  const moreDisabled = color >= 100;

  if ((lessDisabled || moreDisabled) && coloring) {
    clearInterval(coloring);
    setColoring(null);
  }

  const handleSubtractColor = () => {
    const interval = setInterval(() => setMyColor(prevState => ({
      ...prevState,
      [name]: lessDisabled ? 0 : prevState[name] - 1,
    })), 100);

    setColoring(interval);
  };

  const handleAddColor = () => {
    const interval = setInterval(() => setMyColor(prevState => ({
      ...prevState,
      [name]: moreDisabled ? 100 : prevState[name] + 1,
    })), 100);

    setColoring(interval);
  };

  return (
    <div style={{
      borderRadius: '5rem',
      height: '10rem',
      width: '10rem',
      backgroundColor: COLORS[name],
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }} >
      <button
        style={{
          userSelect: 'none',
          backgroundColor: 'transparent',
          height: '10rem',
          width: '5rem',
          borderRadius: '7.5rem 0 0 7.5rem',
          fontSize: '6rem',
          paddingBottom: '0.5rem',
          borderRight: 'none',
          textShadow: '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
          color: lessDisabled ? '#ddd' : '#fff',
          cursor: lessDisabled ? 'not-allowed' : 'pointer',
        }}
        disabled={lessDisabled}
        onMouseDown={handleSubtractColor}
        onTouchStart={handleSubtractColor}
        onMouseUp={() => {
          coloring && clearInterval(coloring)
          setColoring(null);
        }}
        onTouchEnd={() => {
          coloring && clearInterval(coloring)
          setColoring(null);
        }}
      >
        -
      </button>
      <button
        style={{
          userSelect: 'none',
          backgroundColor: 'transparent',
          height: '10rem',
          width: '5rem',
          borderRadius: '0 7.5rem 7.5rem 0',
          fontSize: '6rem',
          paddingBottom: '0.5rem',
          textShadow: '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
          color: moreDisabled ? '#ddd' : '#fff',
          cursor: moreDisabled ? 'not-allowed' : 'pointer',
        }}
        disabled={moreDisabled}
        onMouseDown={handleAddColor}
        onTouchStart={handleAddColor}
        onMouseUp={() => {
          coloring && clearInterval(coloring)
          setColoring(null);
        }}
        onTouchEnd={() => {
          coloring && clearInterval(coloring)
          setColoring(null);
        }}
      >
        +
      </button>
    </div>
  );
}
