import { useState, useEffect } from 'react';

const COLORS = {
  'Cyan': '#00FFFF',
  'Magenta': '#FF00FF',
  'Yellow': '#ffff00',
  'Black': '#000000'
};
export default function ColorToggle({ color, setColor, name }) {
  const [plus, setPlus] = useState(false);
  const [minus, setMinus] = useState(false);


  useEffect(() => {
    while (plus && color <= 100) {
      setInterval(() => setColor(color + 1), 500);
    }
  }, [setColor, color, plus]);

  useEffect(() => {
    while (minus && color >= 0) {
      setInterval(() => setColor(color - 1), 500);
    }
  }, [setColor, color, minus]);

  return (
    <div style={{
      borderRadius: '5rem',
      height: '10rem',
      width: '10rem',
      backgroundColor: COLORS[name],
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }} >
      <button
        disabled={color === 100}
        onMouseDown={() => setPlus(true)}
        onMouseUp={() => setPlus(false)}
      >
        More {name}
      </button>
      <button
        disabled={color === 0}
        onMouseDown={() => setMinus(true)}
        onMouseUp={() => setMinus(false)}
      >
        Less {name}
      </button>
    </div>
  );
}
