export default function ColorToggle({ color, setColor, name}) {

  return (
    <>
      <button disabled={color === 100} onClick={() => setColor(color + 1)}>
        More {name}
      </button>
      <button disabled={color === 0} onClick={() => setColor(color - 1)}>
        Less {name}
      </button>
    </>
  );
}
