import cmykRgb from 'cmyk-rgb';
import { getColorByDate } from '../App';

export default function History() {
  const dates = Array.from({length: 10}, (x, i) => {
    const date = new Date();
    date.setDate(date.getDate() - i);
    return date.toLocaleDateString();
  });

  return dates.map(date => {
    const { cyan, magenta, yellow, black } = getColorByDate(new Date(date).valueOf())

    const score = localStorage.getItem(date) || 'dnf';
    const color = localStorage.getItem(`${date}_color`) || { cyan, magenta, yellow, black };

    const [r, g, b] = cmykRgb([cyan / 100, magenta / 100, yellow / 100, black / 100]);
    const [myR, myG, myB] = cmykRgb([color.cyan / 100, color.magenta / 100, color.yellow / 100, color.black / 100]);

  const gradient = `linear-gradient(to right, rgba(${myR},${myG},${myB},1) 50%, rgba(${r}, ${g}, ${b}, 1) 50%)`;
    return (
      <div
        style={{
          background: gradient,
          height: '10vh',
          width: '100vw',
          display: 'flex',
          alignContent: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          textAlign: 'center',
          textShadow: '-1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000',
          color: '#fff',
        }}>
        {score}
      </div>
    );
  });
}
